import React, { useState } from "react";
import Navbar from "./Navbar.js";
import Stutents from "./Stutents.js";

function App() {
  const [students, setStudents] = useState([]);
  const [student, setStudent] = useState({
    name: "",
    email: "",
    website: "",
    image: "",
    gender: [],
    skills: []
  });
  // function for get data inputed by user
  const handleChange = e => {
    const { name, value } = e.target;
    setStudent(prevStudent => {
      return {
        ...prevStudent,
        [name]: value
      };
    });
  };
  // function for submit data
  const handleSubmit = e => {
    e.preventDefault();
    setStudents(prevStudents => {
      return [...prevStudents, student];
    });
    setStudent({
      name: "",
      email: "",
      website: "",
      image: "",
      gender: [],
      skills: []
    });
  };


  return (
    <div className="App">
      <Navbar />
      <main>
        <form autoComplete="off">
          <div className="inputFld">
            <label htmlFor="name">Name</label>
            <input type="text" id="name"  />
          </div>
          <div className="inputFld">
            <label htmlFor="email">Email</label>
            <input type="email" id="email"  />
          </div>
          <div className="inputFld">
            <label htmlFor="url">Website</label>
            <input type="website" id="website"  />
          </div>
          <div className="inputFld">
            <label htmlFor="image">Image Link</label>
            <input type="text" id="image"  />
          </div>
          <div className="multiInput">
            <label htmlFor="gender">Gender</label>
            <input type="radio" name="gender" value="male"  />Male
            <input type="radio" name="gender" value="female"  />Female
          </div>
          <div className="multiInput">
            <label htmlFor="skill">Skills</label>
            <input type="checkbox" name="skill" value="html"  />HTML
            <input type="checkbox" name="skill" value="css"  />CSS
            <input type="checkbox" name="skill" value="js"  />JavaScript
          </div>
          <div>
            <button type="submit">Enroll Student</button>
            <button type="reset">Clear</button>
          </div>
        </form>
        < Stutents />
      </main >
    </div >
  );
}

export default App;
