import React, { useState } from "react";
import Form from "./Form.js";
import Navbar from "./Navbar.js";
import Stutents from "./Stutents.js";

function App() {
  const [students, setStudents] = useState([]);
  const [student, setStudent] = useState({
    name: "",
    email: "",
    website: "",
    image: "",
    gender: [],
    skills: []
  });
  // function for get data inputed by user
  const handleChange = e => {
    const { name, value } = e.target;
    setStudent(prevStudent => {
      return {
        ...prevStudent,
        [name]: value
      };
    });
  };
  // function for submit data
  const handleSubmit = e => {
    e.preventDefault();
    setStudents(prevStudents => {
      return [...prevStudents, student];
    });
    setStudent({
      name: "",
      email: "",
      website: "",
      image: "",
      gender: [],
      skills: []
    });
  };


  return (
    <div className="App">
      <Navbar />
      <main>
        <Form />
        < Stutents />
      </main >
    </div >
  );
}

export default App;
