import React, { useState } from "react";
import Navbar from "./Navbar.js";
import Stutents from "./Stutents.js";

function App() {
  const [students, setStudents] = useState([]);
  const [student, setStudent] = useState({
    name: "",
    email: "",
    website: "",
    image: "",
    gender: [],
    skills: []
  });
  // function for get data inputed by user
  const handleChange = e => {
    const { name, value } = e.target;
    setStudent(prevStudent => {
      return {
        ...prevStudent,
        [name]: value
      };
    });
  };
  // function for submit data
  const handleSubmit = e => {
    e.preventDefault();
    setStudents(prevStudents => {
      return [...prevStudents, student];
    });
    setStudent({
      name: "",
      email: "",
      website: "",
      image: "",
      gender: [],
      skills: []
    });
  };


  return (
    <div className="App">
      <Navbar />
      <main>
        <form autoComplete="off" onSubmit={handleSubmit}>
          <div className="inputFld">
            <label htmlFor="name">Name</label>
            <input 
            type="text" 
            id="name" onChange={handleChange} />
          </div>
          <div className="inputFld">
            <label htmlFor="email">Email</label>
            <input 
            type="email" 
            id="email" onChange={handleChange} />
          </div>
          <div className="inputFld">
            <label htmlFor="url">Website</label>
            <input 
            type="website" 
            id="website" onChange={handleChange} />
          </div>
          <div className="inputFld">
            <label htmlFor="image">Image Link</label>
            <input 
            type="text" 
            id="image" onChange={handleChange} />
          </div>
          <div className="multiInput">
            <label htmlFor="gender">Gender</label>
            <input 
            type="radio" 
            name="gender" value="male" onChange={handleChange} />Male
            <input 
            type="radio" 
            name="gender" value="female" onChange={handleChange} />Female
          </div>
          <div className="multiInput">
            <label htmlFor="skill">Skills</label>
            <input 
            type="checkbox" 
            name="skill" value="html" onChange={handleChange} />HTML
            <input 
            type="checkbox" 
            name="skill" value="css" onChange={handleChange} />CSS
            <input 
            type="checkbox" 
            name="skill" value="js" onChange={handleChange} />JavaScript
          </div>
          <div>
            <button 
            type="submit">
              Enroll Student</button>
            <button 
            type="reset">
              Clear</button>
          </div>
        </form>
        < Stutents />
      </main >
    </div >
  );
}

export default App;
